//https://www.hackerrank.com/challenges/equal-stacks#include <iostream>#include <algorithm>#include <queue>using namespace std;int main(){    int n1;    int n2;    int n3;    cin >> n1 >> n2 >> n3;    queue<int> que1;    queue<int> que2;    queue<int> que3;    bool ok = false;    int a, total1 = 0, total2 = 0, total3 = 0 ;    for(int h1_i = 0;h1_i < n1;h1_i++){        cin >> a;        que1.push(a);        total1 += a;    }    for(int h2_i = 0;h2_i < n2;h2_i++){        cin >> a;        que2.push(a);        total2 += a;    }    for(int h3_i = 0;h3_i < n3;h3_i++){        cin >> a;        que3.push(a);        total3 += a;    }    while(!que1.empty() && !que2.empty() && !que3.empty()){        if(total1 == total2 && total2 == total3){            ok = true;            cout << total1 << endl;            break;        }else{            int m = max(total1, max(total2, total3));            if(m == total1){                total1 -= que1.front();                que1.pop();            }else if(m == total2){                total2 -= que2.front();                que2.pop();            }else if(m == total3){                total3 -= que3.front();                que3.pop();            }        }    }    if(!ok)cout << "0" << endl;                return 0;}