//https://www.hackerrank.com/challenges/jumping-on-the-clouds-revisited#include <stdio.h>int main(){    int n;    int k;    scanf("%d %d",&n,&k);    int *c = malloc(sizeof(int) * n);    for(int c_i = 0; c_i < n; c_i++){        scanf("%d",&c[c_i]);    }    int e= 100;    int i = 0 ;    do{        if(c[i] == 1) e -= 2;        e -= 1;        i = (i+k)%n;            }while(i != 0);        printf("%d\n", e);            return 0;}