//https://www.hackerrank.com/challenges/sherlock-and-array#include <stdio.h>#include <string.h>#include <math.h>#include <stdlib.h>int k,i,j,n;int a[200001];int main() {        scanf("%d",&k);    for( i = 0; i < k ; i++){        int sum[200001]={0};        scanf("%d",&n);        for( j = 0; j < n ; j++){            scanf("%d",a+j);            if (j == 0)                sum[j]=a[j];            else                sum[j]=sum[j-1]+a[j];        }        //    for (i=0; i<n ; i++) {        //        printf("%d\t",sum[i]);        //    }                int strt = 0,fnsh = n-1, posofans= -1 ,mid;        while(strt<=fnsh){            //printf("entered1 \n");            mid = (strt+fnsh)/2;            if(sum[mid-1]==sum[n-1]-sum[mid]){                //printf("entered2 \n");                posofans=mid;                break;            }            if(sum[mid-1]>sum[n-1]-sum[mid])                fnsh=mid-1;            else                strt=mid+1;        }                if(posofans == -1)            printf("NO\n");        else            printf("YES\n");    }    return 0;}